import React, { useEffect, useState } from 'react';
import './loader.css';

export default function Loader() {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate a delay to allow the loader animation to complete
    const timer = setTimeout(() => {
      setLoading(false);
    }, 3000); // 3 seconds (adjust according to your animation duration)

    return () => clearTimeout(timer); // Clean up the timer
  }, []);

  return (
    <>
      {loading ? (
        <div className="main-main-loader-div">
          <div className="loading-container">
            <div className="loading-text">
              <span>A</span>
              <span>I</span>
              <span>S</span>
              <span>L</span>
              <span>I</span>
              <span>N</span>
              <span></span>
              <span>F</span>
              <span>O</span>
              <span>R</span>
              <span>M</span>
              <span>U</span>
              <span>L</span>
              <span>A</span>
              <span>T</span>
              <span>I</span>
              <span>O</span>
              <span>N</span>
              <span>S</span>
            </div>
          </div>

          <div className="socials">
            <a className="social-link" href="https://twitter.com/aybukeceylan" target="_top">
              <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-twitter">
                <path d="M23 3a10.9 10.9 0 01-3.14 1.53 4.48 4.48 0 00-7.86 3v1A10.66 10.66 0 013 4s-4 9 5 13a11.64 11.64 0 01-7 2c9 5 20 0 20-11.5a4.5 4.5 0 00-.08-.83A7.72 7.72 0 0023 3z" />
              </svg>
            </a>
            <a className="social-link" href="https://www.linkedin.com/in/ayb%C3%BCkeceylan/" target="_top">
              <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-linkedin">
                <path d="M16 8a6 6 0 0 1 6 6v7h-4v-7a2 2 0 0 0-2-2 2 2 0 0 0-2 2v7h-4v-7a6 6 0 0 1 6-6z" />
                <rect x="2" y="9" width="4" height="12" />
                <circle cx="4" cy="4" r="2" />
              </svg>
            </a>
          </div>
        </div>
      ) : (
        <div className="main-content visible">
          {/* Your main content goes here */}
          <h1>Welcome to the site!</h1>
        </div>
      )}
    </>
  );
}
